/*!50003 CREATE*/ /*!50017 DEFINER=`jdpsoluc_l_monrroy`@`%`*/ /*!50003 TRIGGER `documentos_BEFORE_INSERT` BEFORE INSERT ON `documentos` FOR EACH ROW BEGIN

 DECLARE _id_cierre_activo INT;
