     IF 
     new.tipoDocumentoFinal <>  getIdContadorByName('cotizacion')  and
     new.tipoDocumentoFinal <>  getIdContadorByName('eliminado')  and
     new.tipoDocumentoFinal <>  getIdContadorByName('domicilio')  and
