				   ,p_idDocumento , _idTercero ) ;  
                     select max(id)  into _movimiento  
					from cnt_operaciones where idDocumento =p_idDocumento and nombre like '%Nota Debito%';
           
           end if;
           
           	insert into cnt_transacciones 
