DECLARE EXIT HANDLER FOR SQLEXCEPTION
	BEGIN
		select 'error en la base de datos' AS result LIMIT 1; 
	END; 
DECLARE EXIT HANDLER FOR SQLWARNING
	BEGIN
		select  WARNINGS  AS result LIMIT 1; 
	END;
	if nomTable = '' then
